        public bool Enviar_mail()
        {
            try
            {

                switch (this.Tipo_mail)
                {
                    case tipo_mail.validacion:
                        cuerpo = Cargar_cuerpo_validacion();
                        break;
                    case tipo_mail.recupero_contraseña:
                        cuerpo = Cargar_cuerpo_recuperar_contraseña();
                        break;
                    case tipo_mail.notificacion_asignacion_tesina_director:
                        cuerpo = Cargar_cuerpo_asignacion_tesina_director();
                        break;
                    case tipo_mail.notificacion_inicio_tesina_tesista:
                        cuerpo = Cargar_cuerpo_inicio_tesina_tesista();
                        break;
                    default:
                        break;
                }

                var msg = new MailMessage(Credenciales_mail, Destinatarios[0], Asunto, cuerpo);

                for (int i = 1; i < Destinatarios.Count; i++)
                {
                    msg.To.Add(Destinatarios[i]);
                }

                msg.IsBodyHtml = true;

                var smtpClient = new SmtpClient(Smtp_client, Smtp_puerto);
                smtpClient.UseDefaultCredentials = true;
                smtpClient.Credentials = new NetworkCredential(Credenciales_mail, Credenciales_contraseña);
                smtpClient.EnableSsl = true;
                smtpClient.Send(msg);

                return true;
            }
            catch (Exception ex)
            {
                return false;
            }
        }

    }
